-- ==================================================================================================
-- CONVERT
-- ==================================================================================================/*Estas funciones convierten una expresión de un tipo de datos a otro.*/-- Estilos de fecha y horaSELECT CONVERT(nvarchar(10),GETDATE(),101) -- 10/19/2022SELECT CONVERT(nvarchar(10),GETDATE(),102) -- 2022.10.19SELECT CONVERT(nvarchar(10),GETDATE(),103) -- 19/10/2022SELECT CONVERT(nvarchar(10),GETDATE(),104) -- 19.10.2022SELECT CONVERT(nvarchar(10),GETDATE(),105) -- 19-10-2022SELECT CONVERT(nvarchar(10),GETDATE(),106) -- 19 Oct 202SELECT CONVERT(nvarchar(10),GETDATE(),107) -- Oct 19, 20SELECT CONVERT(nvarchar(10),GETDATE(),108) -- 12:37:39SELECT CONVERT(nvarchar(10),GETDATE(),110) -- 10-19-2022SELECT CONVERT(nvarchar(10),GETDATE(),111) -- 2022/10/19SELECT CONVERT(nvarchar(10),GETDATE(),114) -- 12:39:26:4-- Estilos float y real-- Siempre 8 dígitos. Utilícelo siempre en notación científica.SELECT CONVERT(NVARCHAR(50),559697.5639,1)  -- Siempre 16 dígitos. Utilícelo siempre en notación científica.SELECT CONVERT(NVARCHAR(50),559697.5639,2)-- Siempre 17 dígitos. Se usa para la conversión sin pérdida de información.SELECT CONVERT(NVARCHAR(50),559697.5639,3)DECLARE @Number MONEY = 559697.5639;-- Separadores de millar cada tres dígitos a la izquierda del separador decimal -- y dos dígitos a la derecha del separador decimalSELECT CONVERT(NVARCHAR(50),@Number,1)-- Sin separadores de millar cada tres dígitos a la izquierda del separador decimal -- y cuatro dígitos a la derecha del separador decimalSELECT CONVERT(NVARCHAR(50),@Number,2)-- Equivalente al estilo 2 al convertir a char(n) o varchar(n)SELECT CONVERT(NVARCHAR(50),@Number,3)-- En estos ejemplos se recupera el nombre de aquellos productos que tienen un 3 como primer dígito-- del precio y se convierte sus valores ListPrice en int.SELECT p.Name , p.ListPrice FROM Production.Product AS pWHERE CONVERT(INT, p.ListPrice) LIKE '33%'